import React from 'react'
export default function TodoItems({ todo,onDelete }) {
    return (
        <div className="task">

            <h4>{todo.taskName} </h4>
            <p> {todo.taskDesc} </p>
            <button className='btn btn-danger' onClick={()=>onDelete(todo)}>Delete</button>
        </div>
    );
}
